(36, 1, 12, 'Planning and Requirement Analysis', '<ul>\r\n<li>Identify key features and workflows (e.g., course creation, student management).</li>\r\n<li>Research existing LMS plugins to analyze functionality and user needs.</li>\r\n<li>Define user roles (admin, instructor, student) and their respective permissions.</li>\r\n</ul>', 75, 14, '2025-03-01', '2025-03-01', NULL, 1, 1, NULL, '2025-02-05 23:50:16', '2025-02-06 00:09:40'),
(37, 1, 12, 'Project Setup', '<ul>\r\n<li>Set up a local WordPress environment for development.</li>\r\n<li>Create a structured folder system (<code>includes/</code>, <code>assets/</code>, <code>templates/</code>, <code>languages/</code>).</li>\r\n<li>Initialize the main plugin file with proper WordPress headers.</li>\r\n</ul>', 72, 17, '2025-03-03', '2025-03-08', NULL, 0, 1, NULL, '2025-02-05 23:51:39', '2025-02-05 23:51:39'),
(38, 1, 12, 'Make Course Management Module', '<ul>\r\n<li>Develop a drag-and-drop course builder with support for lessons, modules, and quizzes.</li>\r\n<li>Add functionality to upload multimedia content (videos, PDFs, etc.).</li>\r\n<li>Implement course categorization and tagging for better organization.</li>\r\n</ul>', 73, 16, '2025-03-10', '2025-03-15', NULL, 0, 1, NULL, '2025-02-05 23:53:20', '2025-02-06 00:10:09'),
(39, 1, 12, 'User Roles and Permission', '<ul>\r\n<li>Create role-specific dashboards for administrators, instructors, and students.</li>\r\n<li>Set up permissions for managing courses, viewing progress, and accessing reports.</li>\r\n</ul>', 74, 15, '2025-03-17', '2025-03-18', NULL, 1, 1, NULL, '2025-02-05 23:54:05', '2025-02-06 00:10:35'),
(40, 1, 12, 'Progress Tracking and Analytics', '<ul>\r\n<li>Develop a system to track student progress, grades, and completion rates.</li>\r\n<li>Build instructor dashboards to display detailed course analytics and reports.</li>\r\n</ul>', 75, 14, '2025-03-19', '2025-03-21', NULL, 1, 1, NULL, '2025-02-05 23:55:03', '2025-02-06 00:11:01'),
(41, 1, 12, 'Interactive Learning Features', '<ul>\r\n<li>Design quiz functionality with multiple question types (MCQs, True/False, etc.).</li>\r\n<li>Add support for assignments with file upload and peer-review options.</li>\r\n<li>Implement real-time chat and discussion forums for student engagement.</li>\r\n</ul>', 0, 17, '2025-03-22', '2025-03-26', NULL, 1, 1, NULL, '2025-02-05 23:56:42', '2025-02-06 00:09:13'),
(42, 1, 12, 'Certification System', '<ul>\r\n<li>Develop customizable certificates for course completion.</li>\r\n<li>Integrate with digital badge services for gamification.</li>\r\n</ul>', 0, 16, '2025-03-27', '2025-03-28', NULL, 1, 1, NULL, '2025-02-05 23:57:58', '2025-02-06 00:09:05'),
(43, 1, 12, 'Payment Integration', '<ul>\r\n<li>Set up payment gateways like PayPal and Stripe for paid courses.</li>\r\n<li>Develop options for memberships and course bundles.</li>\r\n</ul>', 0, 15, '2025-03-28', '2025-03-29', NULL, 1, 1, NULL, '2025-02-06 00:00:58', '2025-02-06 00:09:00'),
(44, 1, 12, 'Testing and Debugging', '<ul>\r\n<li>Test across WordPress versions, themes, and plugins for compatibility.</li>\r\n<li>Perform usability testing for course creation, enrollment, and progress tracking.</li>\r\n<li>Debug issues in payment processing, user roles, and engagement features.</li>\r\n</ul>', 0, 15, '2025-04-01', '2025-04-03', NULL, 0, 1, NULL, '2025-02-06 00:06:05', '2025-02-06 00:08:53'),
